import { useState } from "react";
import { Drawer } from "antd";
import HeaderNavItem from "./HeaderNavItem";
import HamMenu from "./HamMenu";

export default function HeaderNavBar() {
    const [isOnTouch, setIsOnTouch] = useState(false);
    return (
        <div>
            <HamMenu
                onClick={() => setIsOnTouch(!isOnTouch)}
                isOnTouch={isOnTouch}
            />
            <div className="nav-bar collapse-mobile">
                <HeaderNavItem to="/" className="nav-item" activeClassName="nav-item--active">
                    首頁
                </HeaderNavItem>
                <HeaderNavItem to="/story" className="nav-item" activeClassName="nav-item--active">
                    品牌故事
                </HeaderNavItem>
                <HeaderNavItem to="/problem" className="nav-item" activeClassName="nav-item--active">
                    常見Q&A
                </HeaderNavItem>
                <HeaderNavItem to="/" className="nav-item" activeClassName="nav-item--active">
                    Lighting
                </HeaderNavItem>
                <HeaderNavItem to="/" className="nav-item" activeClassName="nav-item--active">
                    Textile
                </HeaderNavItem>
                <HeaderNavItem to="/" className="nav-item" activeClassName="nav-item--active">
                    Furniture
                </HeaderNavItem>
            </div>
        </div>
    );
}
