import { useContext } from "react";
import { Link } from "react-router-dom";
import { StoreContext } from "../store";
import CartSummary from "./CartSummary";
import { pageContentsSet, activeNavItemSet } from "../actions";
import { getJSON } from "../api";

export default function Header({ title }) {
  const { dispatch } = useContext(StoreContext);
  const onClickHeader = () => {
    pageContentsSet(dispatch, "NORDIC NEST Shopping Cart", getJSON("/"));
    activeNavItemSet(dispatch, "/");
  };

  return (
    <header className="header">
      <div className="header-wrap">
        <div className="header-text" onClick={onClickHeader}>
          <Link to="/">
            <img className="header-logo" src="https://scontent.ftpe12-1.fna.fbcdn.net/v/t1.18169-9/944790_10152076127029228_1034845300_n.jpg?_nc_cat=107&ccb=1-3&_nc_sid=09cbfe&_nc_ohc=0VJVzDnPrakAX8whRxf&_nc_ht=scontent.ftpe12-1.fna&oh=fe85d8626bc339ee5401723f3e0af670&oe=609C13A2"></img>
            {/* <h1 className="header-title">{title}</h1> */}
          </Link>
          {/* <p className="header-slogan">An example made by Create-React-App.</p> */}
        </div>
        <CartSummary />
      </div>

      {/* <hr className="hr-header-line" /> */}
    </header>
  );
}
